import "../style/pokedex.css";
import { useState, useEffect } from "react";

function Pokedex() {
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [pokemons, setPokemons] = useState([]);

    useEffect(() => {
        fetch('https://pokeapi.co/api/v2/pokemon/1')
        .then(res => res.json())
        .then(data => {
            setIsLoaded(true);
            setPokemons(data);
        },
        (error) => {
            setIsLoaded(true);
            setError(error);    
        }
        )}, []
    );

    const handleChange = event => {
        fetch('https://pokeapi.co/api/v2/pokemon/' + event.target.value)
        .then(res => res.json())
        .then(data => {
            setPokemons([...pokemons, data]);
        });

        console.log(pokemons);
    };  

    if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <div className="pokedex">
                    <h1>Pokedex</h1>    
                    <div className="pokemon">
                        <h3>{pokemons.name.charAt(0).toUpperCase() + pokemons.name.slice(1)}</h3>
                        <img className="poke-sprite" src={pokemons.sprites.front_default} alt={pokemons.name} />
                    </div>
                    <input type="number" min="1" onChange={handleChange}/> 
                </div>
            );
        }
}

export default Pokedex;
