function Pokemon(props){
    return (
        <div className="pokemon">
            <h3>{props.name.charAt(0).toUpperCase() + props.name.slice(1)}</h3>
            <img className="poke-sprite" src={props.sprites.front_default} alt={props.name} />
        </div>
    )
}

export default Pokemon;
